import React, { Component } from "react";

export default class quote extends Component {
  constructor(props) {
    super(props);

    this.state = {
      quoteData: [],
      quote: "",
      author: "",
    };
    this.randomQuote = this.randomQuote.bind(this);
    this.handleClick = this.handleClick.bind(this);
  }

  componentDidMount() {
    const API =
      "https://gist.githubusercontent.com/camperbot/5a022b72e96c4c9585c32bf6a75f62d9/raw/e3c6895ce42069f0ee7e991229064f167fe8ccdc/";
    fetch(API)
      .then((response) => response.json())
      .then((data) => {
        this.setState({
          quoteData: data.quotes,
        });
      })
      .catch((error) => console.log("Error", error));

    this.randomQuote();
  }

  randomQuote() {
    const randomNumber = Math.floor(Math.random() * this.quoteData.length);
    return this.quoteData[randomNumber];
  }

  handleClick() {
    const oneRandomQuote = this.randomQuote();
    this.setState({
      quote: oneRandomQuote.quote,
      author: oneRandomQuote.author,
    });
  }

  render() {
    return (
      <div id="quote-box">
        <h1 id="text">{this.state.quote}</h1>
        <h3 id="author">- {this.state.author}</h3>
        <Buttons handleClick={this.handleClick} />
      </div>
    );
  }
}
